import { Row, Col } from "antd";
import { withTranslation } from "react-i18next";
import { SvgIcon } from "../../common/SvgIcon";
import {
  FooterSection,
  LogoContainer,
  LogoContainerText,
  NavLink,
  CustomNavLinkSmall,
  Chat,
  Copyright,
} from "./styles";

import Grid from '@material-ui/core/Grid';

interface SocialLinkProps {
  href: string;
  src: string;
}

const Footer = ({ t }: any) => {

  const SocialLink = ({ href, src }: SocialLinkProps) => {
    return (
      <a
        href={href}
        target="_blank"
        rel="noopener noreferrer"
        key={src}
        aria-label={src}
      >
        <SvgIcon src={src} width="25px" height="25px" />
      </a>
    );
  };

  return (
    <FooterSection>
        <Row>
          
          <Col lg={9} md={9} sm={9} xs={24} offset={0}>   
              <LogoContainer>
                <SvgIcon src="logo.svg" width="50px" height="50px" />
                <LogoContainerText> OKTO </LogoContainerText>
              </LogoContainer>
          </Col>

          <Col lg={6} md={6} sm={6} xs={0} offset={0}></Col>

          <Col lg={9} md={9} sm={9} xs={24} offset={0}>
            <NavLink>
              <a href="" target="_blank">
                <CustomNavLinkSmall>
                  <SvgIcon src="discord.svg" width="25px" height="25px" />
                </CustomNavLinkSmall>
              </a>

              <a href="https://twitter.com/solanawiba" target="_blank">
                <CustomNavLinkSmall>
                  <SvgIcon src="twitter.svg" width="25px" height="25px" />
                </CustomNavLinkSmall>
              </a>
            </NavLink>
          </Col>

      </Row>

      <Row>
        <Col lg={9} md={9} sm={9} xs={24} offset={0}>   
            <LogoContainer>
              <Chat> Â©2021 Okto. All rights reserved. </Chat>
            </LogoContainer>
        </Col>
        <Col lg={15} md={15} sm={15} xs={0} offset={0}></Col>
      </Row>
    </FooterSection>
  );
};

export default withTranslation()(Footer);
