import { Row } from "antd";
import React, {useState} from "react";

import { render } from "react-dom";
import { withTranslation } from "react-i18next";
import RoadmapContentBlock from "../ContentBlock/RoadmapContentBlock";

import {
  GenContainer,
  BlockContainer,
  ContentWrapperTitle,
  FaqContainer,
  FaqContent,
  FaqContentWrapper,
} from "./styles";



const Faq = () => {

  const [visible1, setVisible1] = useState(false);
  const [visible2, setVisible2] = useState(false);

  return (
    <GenContainer>
      <BlockContainer>
          <Row justify="center" align="middle">
            <ContentWrapperTitle>
                <h1>FAQ</h1>
            </ContentWrapperTitle>
          </Row>

          <FaqContainer onClick={() => setVisible1(!visible1)}>
            <FaqContentWrapper>
              <h6>What is Okto ?</h6>
              { visible1 &&  <FaqContent>
                4,386 unique, punchy, algorithmically generated Oktos - stored with proof of ownership stored on the Solana blockchain.
              </FaqContent>    }
            </FaqContentWrapper>
          </FaqContainer>
              

          <FaqContainer onClick={() => setVisible2(!visible2)}>
            <FaqContentWrapper>
              <h6>How do I buy an Okto ?</h6>
              { visible2 &&  <FaqContent>
                Simple! Connect your Phantom Wallet and follow the instructions on screen on the day we launch.
                <br/> 1. Download the Phantom Wallet extension for your browser (Google Chrome recommended).
                <br/> 2. Buy Solana (SOL) on an exchange like Coinbase, Binance, FTX etc and send it to your Phantom wallet Public Address.
                <br/> 3. Connect your Wallet to our website by clicking on the Connect button.
                <br/> 4. Click the 'Mint for 1 SOL' button on www.okto.com and approve the transaction in your Wallet. 
              </FaqContent>    }
            </FaqContentWrapper>
          </FaqContainer>

      </BlockContainer>
    </GenContainer>
  );
};

export default withTranslation()(Faq);
