import styled from "styled-components";

export const GenContainer = styled("section")`
  position: relative;
  width: 100%;
  height: 400px;
  margin-right: auto;
  margin-left: auto;
  padding: 0rem 1rem;
  backgroundImage: 'url('../../common/Img/Join.jpeg')'; 

  @media only screen and (max-width: 1024px) {
    padding: 2rem 0rem;
    text-align: center;
  }

  @media only screen and (max-width: 768px) {
    padding: 2rem 0rem;
    text-align: center;
  }

  @media only screen and (max-width: 414px) {
    padding: 2rem 0rem;
    text-align: center;
  }
`;

export const BlockContainer = styled("section")`
  position: relative;
  margin: 1rem;
  padding: 0rem;
  width: 100%;
  display: flex;
  justify-content: center;

  @media only screen and (max-width: 1024px) {
    padding: 8rem 0 6rem;
  }

  @media only screen and (max-width: 768px) {
    padding: 4rem 0 3rem;
  }
`;

export const Span = styled("span")<any>`
  display: flex;
  justify-content: center;
  text-align: left;
`;

export const ButtonContainer = styled("div")`
min-width: 200px;
  @media only screen and (max-width: 414px) {
    padding-top: 0.75rem;
  }
`;


export const Content = styled("p")`
  margin-top: 1.5rem;
`;

export const Container = styled("div")`
  position: relative;
  max-width: 700px;
`;

export const TextWrapper = styled("div")`
  border-radius: 3rem;
  max-width: 400px;
`;
